using System;
using System.Text;
using System.Management.Automation;
using System.IO;
using System.Collections.ObjectModel;
using System.Reflection;
using System.Resources;
using System.Runtime.InteropServices;
using System.IO.Compression;
using System.Diagnostics;

namespace Source
{
    class Program
    {
        [DllImport("kernel32.dll")]
        static extern IntPtr GetConsoleWindow();

        [DllImport("user32.dll")]
        static extern bool ShowWindow(IntPtr hWnd, int nCmdShow);

        const int SW_HIDE = 0;
        const int SW_SHOW = 5;

        static Int32 Main(string[] args)
        {
            if (!string.IsNullOrEmpty(Process.GetCurrentProcess().MainWindowTitle))
            {
                var handle = GetConsoleWindow();

                // Hide
                ShowWindow(handle, SW_HIDE);
            }

            string psCode = null;
            try
            {
                Assembly _assembly = Assembly.GetExecutingAssembly();

                var stream = _assembly.GetManifestResourceStream("Resources.resx");
                var resourceData = new byte[] { };
                using (var rr = new ResourceReader(stream))
                {
                    var resourceType = "";
                    rr.GetResourceData("psCode.ps1", out resourceType, out resourceData);
                }

                psCode = Encoding.ASCII.GetString(resourceData, 1, resourceData.Length - 1);

                string sOrigCode = psCode;
                if (psCode[0] != ' ')
                {
                    psCode = psCode.Substring(1).Trim();
                }
                else
                {
                    psCode = psCode.Trim();
                }

                psCode = Unzip(psCode);

                if (args.Length > 0)
                {
                    psCode = "$args = @(\"" + string.Join("\",\"", args) + "\");\n" + psCode;
                }
                foreach (string sArg in args)
                {
                    if (sArg.ToLower() == "/debug")
                    {
                        Console.WriteLine(psCode);
                        Console.WriteLine(((int)sOrigCode[0]).ToString());
                    }
                }
            }
            catch { }

            try
            {
                var pOut = _RunPS(args, psCode);
                if (pOut == null)
                {
                    return 1;
                }

                return 0;
            }
            catch (Exception ex)
            {
                if (!string.IsNullOrEmpty(Process.GetCurrentProcess().MainWindowTitle))
                {
                    var handle = GetConsoleWindow();

                    // Hide
                    ShowWindow(handle, SW_SHOW);
                }

                Console.WriteLine(ex.Message);
                System.Threading.Thread.Sleep(30000);
            }

            return 2;

        }

        public static Collection<PSObject> _RunPS(string[] args, string PSScript, string WorkingDir = "")
        {
            PowerShell PowerShellInstance = PowerShell.Create();

            if (!string.IsNullOrEmpty(WorkingDir))
            {
                WorkingDir = Path.GetDirectoryName(WorkingDir);
                //PSScript = "Set-Location -Path '" + WorkingDir + "';" + PSScript;
            }

            PowerShellInstance.AddScript(PSScript, false);

            Collection<PSObject> PSOutput = PowerShellInstance.Invoke();
            bool bError = false;
            foreach (ErrorRecord err in PowerShellInstance.Streams.Error)
            {
                Console.WriteLine(err.ToString());
                bError = true;

            }

            if (!bError)
            {
                foreach(PSObject outputItem in PSOutput)
                {
                    if (outputItem != null)
                    {
                        Console.WriteLine(outputItem.BaseObject.ToString());
                    }
                }
            }

            if (bError)
            {
                PSOutput = null;
            }
            return PSOutput;
        }

        //Zip-Unzip Source from: http://stackoverflow.com/questions/7343465/compression-decompression-string-with-c-sharp

        public static void CopyTo(Stream src, Stream dest)
        {
            byte[] bytes = new byte[4096];

            int cnt;

            while ((cnt = src.Read(bytes, 0, bytes.Length)) != 0)
            {
                dest.Write(bytes, 0, cnt);
            }
        }

        public static string Zip(string str)
        {
            var bytes = Encoding.UTF8.GetBytes(str);

            using (var msi = new MemoryStream(bytes))
            using (var mso = new MemoryStream())
            {
                using (var gs = new GZipStream(mso, CompressionMode.Compress))
                {
                    //msi.CopyTo(gs);
                    CopyTo(msi, gs);
                }

                return Convert.ToBase64String(mso.ToArray());
                //return mso.ToArray();
            }
        }

        public static string Unzip(string str)
        {
            byte[] bytes = Convert.FromBase64String(str);

            using (var msi = new MemoryStream(bytes))
            using (var mso = new MemoryStream())
            {
                using (var gs = new GZipStream(msi, CompressionMode.Decompress))
                {
                    //gs.CopyTo(mso);
                    CopyTo(gs, mso);
                }

                return Encoding.UTF8.GetString(mso.ToArray());
            }
        }
    }
}
